DROP TABLE IF EXISTS `nd_blog_article`;

CREATE TABLE `nd_blog_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `article_key` char(16) NOT NULL COMMENT '文章标识',
  `article_title` varchar(150) NOT NULL DEFAULT '' COMMENT '标题',
  `article_author` varchar(20) NOT NULL DEFAULT '' COMMENT '作者',
  `article_info` varchar(250) NOT NULL DEFAULT '' COMMENT '简介',
  `article_cover` varchar(150) NOT NULL DEFAULT '' COMMENT '封面',
  `article_cate` int(11) NOT NULL DEFAULT '0' COMMENT '分类ID',
  `article_tags` varchar(250) NOT NULL DEFAULT '' COMMENT '标签',
  `article_origin` varchar(1000) NOT NULL DEFAULT '' COMMENT '原文链接',
  `article_sort` int(11) NOT NULL DEFAULT '0' COMMENT '排序',
  `article_content` mediumtext NOT NULL COMMENT '内容',
  `article_view` int(11) NOT NULL DEFAULT '0' COMMENT '浏览人数',
  `article_ip` varchar(20) NOT NULL COMMENT '访问IP',
  `article_status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态，0：待发布，1：发布',
  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) NOT NULL DEFAULT '0' COMMENT '更新时间',
  `delete_time` int(11) NOT NULL DEFAULT '0' COMMENT '删除时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;



-- --------------------------------------------------------

DROP TABLE IF EXISTS `nd_blog_cate`;

CREATE TABLE `nd_blog_cate` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cate_name` varchar(30) NOT NULL COMMENT '分类',
  `cate_flag` varchar(20) NOT NULL DEFAULT '' COMMENT '分类标识',
  `cate_info` varchar(150) NOT NULL DEFAULT '' COMMENT '分类介绍',
  `cate_sort` int(11) NOT NULL DEFAULT '0' COMMENT '排序',
  `cate_status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态',
  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) NOT NULL DEFAULT '0' COMMENT '更新时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `cate_flag` (`cate_flag`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;



