DROP TABLE IF EXISTS `nd_manage_member_group`;

CREATE TABLE `nd_manage_member_group` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '用户组id,自增主键',
  `group_name` varchar(20) NOT NULL DEFAULT '' COMMENT '用户组中文名称',
